mapper.select:

select 

from experimentclauses
where experimentscoresNo = #{experimentscoresno,jdbcType=VARCHAR}
  and experimentNo = #{experimentno,jdbcType=VARCHAR}
  and experimentclauseNo = #{experimentclauseno,jdbcType=VARCHAR}

mapper.delete:

delete from experimentclauses
where experimentscoresNo = #{experimentscoresno,jdbcType=VARCHAR}
  and experimentNo = #{experimentno,jdbcType=VARCHAR}
  and experimentclauseNo = #{experimentclauseno,jdbcType=VARCHAR}

mapper.update:

update experimentclauses


    title = #{title,jdbcType=VARCHAR},


    evaluate = #{evaluate,jdbcType=VARCHAR},


    num = #{num,jdbcType=INTEGER},


    bili = #{bili,jdbcType=REAL},


    score = #{score,jdbcType=REAL},


where experimentscoresNo = #{experimentscoresno,jdbcType=VARCHAR}
  and experimentNo = #{experimentno,jdbcType=VARCHAR}
  and experimentclauseNo = #{experimentclauseno,jdbcType=VARCHAR}


update experimentclauses
set title = #{title,jdbcType=VARCHAR},
  evaluate = #{evaluate,jdbcType=VARCHAR},
  num = #{num,jdbcType=INTEGER},
  bili = #{bili,jdbcType=REAL},
  score = #{score,jdbcType=REAL}
where experimentscoresNo = #{experimentscoresno,jdbcType=VARCHAR}
  and experimentNo = #{experimentno,jdbcType=VARCHAR}
  and experimentclauseNo = #{experimentclauseno,jdbcType=VARCHAR}
